import React, { useCallback, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';
import { authUtils, normalizeRole } from '../utils/auth';

const Dashboard: React.FC = () => {
  const navigate = useNavigate();
  const [userName, setUserName] = useState<string>('Usuario');
  const [userEmail, setUserEmail] = useState<string>('');
  const [userRole, setUserRole] = useState<string>('usuario');
  const [profileImage, setProfileImage] = useState<string>('');
  const [sidebarOpen, setSidebarOpen] = useState<boolean>(false);
  const [accountModalOpen, setAccountModalOpen] = useState<boolean>(false);

  const loadUser = useCallback(async () => {
    const userId = localStorage.getItem('userId');
    if (!userId) {
      navigate('/');
      return;
    }

    try {
      const resp = await api.get(`/usuarios/${userId}`);
      const user = resp.data.usuario || resp.data;
      setUserName(user?.Nombre || 'Usuario');
      setUserEmail(user?.Email || '');
      setUserRole(user?.Rol || 'usuario');
      authUtils.setUserRole(user?.Rol || 'usuario');

      const base = api.defaults?.baseURL || '';
      if (user?.imagen_perfil) setProfileImage(`${base}${user.imagen_perfil}`);
      else setProfileImage(`${base}/static/default-profile.png`);
    } catch (err) {
      console.error('Error cargando usuario', err);
    }
  }, [navigate]);

  useEffect(() => {
    loadUser();
  }, [loadUser]);

  const navigateTo = (path: string) => navigate(path);

  const handleLogout = () => {
    authUtils.clearAll();
    window.location.href = '/';
  };

  return (
    <div style={{ minHeight: '100vh', background: 'var(--bg-gradient)', color: 'var(--text)', fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif" }}>
      <header style={{ padding: '18px 28px', display: 'flex', justifyContent: 'space-between', alignItems: 'center', background: 'var(--overlay)', backdropFilter: 'blur(6px)' }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: 16 }}>
          <button onClick={() => navigate('/')} style={{ background: 'none', border: 'none', color: 'var(--muted)', fontSize: 20, cursor: 'pointer' }}>←</button>
          <h1 style={{ margin: 0, fontSize: 22 }}>Dashboard</h1>
        </div>

        <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
          <div style={{ textAlign: 'right' }}>
            <div style={{ fontWeight: 700 }}>{userName}</div>
            <div style={{ color: 'var(--muted)', fontSize: 12 }}>{normalizeRole(userRole)}</div>
          </div>
          <img src={profileImage} alt="perfil" style={{ width: 44, height: 44, borderRadius: '50%', objectFit: 'cover', border: '2px solid rgba(255,255,255,0.06)' }} />
        </div>
      </header>

      <main style={{ padding: 40, maxWidth: 1100, margin: '0 auto' }}>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(260px, 1fr))', gap: 20 }}>
          <div style={{ background: 'var(--card-bg)', padding: 20, borderRadius: 12 }}>
            <h2 style={{ color: 'var(--accent)', marginTop: 0 }}>Clases Programadas</h2>
            <p style={{ color: 'var(--muted)' }}>Ver y administrar las clases disponibles.</p>
            <button onClick={() => navigateTo('/clases-programadas')} className="btn" style={{ marginTop: 12 }}>Ir</button>
          </div>

          <div style={{ background: 'var(--card-bg)', padding: 20, borderRadius: 12 }}>
            <h2 style={{ color: 'var(--accent)', marginTop: 0 }}>Ejercicios y Clases</h2>
            <p style={{ color: 'var(--muted)' }}>Explorar ejercicios y contenidos.</p>
            <button onClick={() => navigateTo('/ejercicios-y-clases')} className="btn" style={{ marginTop: 12 }}>Ir</button>
          </div>

          <div style={{ background: 'var(--card-bg)', padding: 20, borderRadius: 12 }}>
            <h2 style={{ color: 'var(--accent)', marginTop: 0 }}>Usuarios</h2>
            <p style={{ color: 'var(--muted)' }}>Gestionar usuarios del sistema.</p>
            <button onClick={() => navigateTo('/usuarios')} className="btn" style={{ marginTop: 12 }}>Ir</button>
          </div>
        </div>

        <section style={{ marginTop: 28, background: 'var(--card-bg)', padding: 18, borderRadius: 12 }}>
          <h3 style={{ margin: 0, color: 'var(--text)' }}>Bienvenido, {userName}.</h3>
          <p style={{ color: 'var(--muted)', marginTop: 6 }}>Email: {userEmail}</p>
          <div style={{ marginTop: 12 }}>
            <button onClick={() => setAccountModalOpen(true)} className="btn" style={{ marginRight: 10 }}>Mi cuenta</button>
            <button onClick={handleLogout} className="btn btn-secondary">Cerrar sesión</button>
          </div>
        </section>
      </main>

      {/* Sidebar / Modal simple implementations */}
      {sidebarOpen && (
        <div style={{ position: 'fixed', inset: 0, background: 'rgba(0,0,0,0.5)', zIndex: 900 }} onClick={() => setSidebarOpen(false)} />
      )}

      {accountModalOpen && (
        <div style={{ position: 'fixed', inset: 0, display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 1000 }}>
          <div style={{ background: 'var(--card-bg)', padding: 20, borderRadius: 12, width: '90%', maxWidth: 480 }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <h4 style={{ margin: 0 }}>Mi Cuenta</h4>
              <button onClick={() => setAccountModalOpen(false)} style={{ background: 'none', border: 'none', fontSize: 18, cursor: 'pointer' }}>×</button>
            </div>

            <div style={{ marginTop: 12 }}>
              <div style={{ display: 'flex', gap: 12, alignItems: 'center' }}>
                <img src={profileImage} alt="perfil" style={{ width: 72, height: 72, borderRadius: '50%', objectFit: 'cover' }} />
                <div>
                  <div style={{ fontWeight: 700 }}>{userName}</div>
                  <div style={{ color: 'var(--muted)' }}>{userEmail}</div>
                  <div style={{ color: 'var(--muted)', marginTop: 6 }}>Rol: {normalizeRole(userRole)}</div>
                </div>
              </div>

              <div style={{ marginTop: 18, display: 'flex', gap: 8 }}>
                <button className="btn" onClick={() => { setAccountModalOpen(false); navigateTo('/usuarios'); }}>Editar</button>
                <button className="btn btn-secondary" onClick={() => { authUtils.clearAll(); window.location.href = '/'; }}>Salir</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
