import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { ExerciseCategory } from '../types';
import api from '../services/api.ts';

interface Clase {
  id: number;
  nombre: string;
  descripcion: string;
  duracion: string;
  fecha_horario_al_que_va: string;
}

const EjerciciosYClases: React.FC = () => {
  const navigate = useNavigate();
  const [expandedCategories, setExpandedCategories] = useState<Set<string>>(new Set());
  const [classes, setClasses] = useState<Clase[]>([]);
  const [selectedExercise, setSelectedExercise] = useState<any>(null);
  const [selectedClase, setSelectedClase] = useState<Clase | null>(null);
  const [showAllClases, setShowAllClases] = useState(false);

  useEffect(() => {
    const loadClases = async () => {
      try {
        console.log('ðŸ”„ Iniciando carga de clases desde /clase...');
        const response = await api.get('/clase');
        console.log('âœ… Respuesta recibida del servidor');
        console.log('   - Status:', response.status);
        console.log('   - Data type:', typeof response.data);
        console.log('   - Is Array:', Array.isArray(response.data));
        console.log('   - Data completa:', response.data);
        
        const data = response.data;
        
        let clasesList: Clase[] = [];
        
        if (Array.isArray(data)) {
          console.log('âœ“ Response es un array directo, cantidad:', data.length);
          clasesList = data as Clase[];
        } else if (data && Array.isArray(data.clases)) {
          console.log('âœ“ Data.clases es array, cantidad:', data.clases.length);
          clasesList = data.clases as Clase[];
        } else if (data && Array.isArray(data.results)) {
          console.log('âœ“ Data.results es array, cantidad:', data.results.length);
          clasesList = data.results as Clase[];
        } else if (data && data.data && Array.isArray(data.data)) {
          console.log('âœ“ Data.data es array, cantidad:', data.data.length);
          clasesList = data.data as Clase[];
        } else {
          console.warn('âš ï¸ No se encontrÃ³ formato reconocido para clases');
          console.log('   Keys disponibles:', Object.keys(data));
        }
        
        console.log('ðŸ“Š Clases cargadas:', clasesList.length);
        clasesList.forEach((c, idx) => {
          console.log(`   [${idx}] nombre="${c.nombre}", fecha="${c.fecha_horario_al_que_va}"`);
        });
        
        setClasses(clasesList);
      } catch (error: any) {
        console.error('âŒ Error cargando clases:', error.message);
        console.error('   Status:', error.response?.status);
        console.error('   Data:', error.response?.data);
        console.error('   Error completo:', error);
        setClasses([]);
      }
    };

    loadClases();
  }, []);

  const toggleCategory = (category: string) => {
    const newExpanded = new Set(expandedCategories);
    if (newExpanded.has(category)) {
      newExpanded.delete(category);
    } else {
      newExpanded.add(category);
    }
    setExpandedCategories(newExpanded);
  };

  const exerciseCategories: ExerciseCategory[] = [
  {
    id: 'upper',
    title: 'Parte Superior del Cuerpo',
    exercises: [
      // HOMBROS
      {
        id: 1,
        title: 'Press militar con mancuernas o barra',
        description: 'Fortalece deltoides y trÃ­ceps. Levanta el peso desde los hombros hasta la extensiÃ³n completa por encima de la cabeza, manteniendo el core activado y la espalda recta.',
        videoUrl: 'https://www.youtube.com/embed/2yjwXTZQDDI',
        category: 'upper'
      },
      {
        id: 2,
        title: 'Elevaciones laterales',
        description: 'Trabaja los deltoides laterales. Con mancuernas a los lados, eleva los brazos hacia los lados hasta la altura de los hombros, manteniendo una ligera flexiÃ³n en los codos.',
        videoUrl: 'https://www.youtube.com/embed/3VcKaXpzqRo',
        category: 'upper'
      },
      {
        id: 3,
        title: 'Elevaciones frontales',
        description: 'Enfoca en deltoides frontales. Levanta las mancuernas al frente hasta la altura de los hombros, alternando o simultÃ¡neamente, controlando el movimiento.',
        videoUrl: 'https://www.youtube.com/embed/2lGA7Ce89e8',
        category: 'upper'
      },
      {
        id: 4,
        title: 'PÃ¡jaros o vuelos inversos',
        description: 'Para deltoides posteriores. InclÃ­nate hacia adelante con la espalda recta y eleva los brazos hacia los lados, enfocÃ¡ndote en los hombros traseros.',
        videoUrl: 'https://www.youtube.com/embed/T7gWBKwmUek',
        category: 'upper'
      },
      {
        id: 5,
        title: 'Plancha con toque de hombros',
        description: 'Activa hombros y core. En posiciÃ³n de plancha alta, toca alternativamente cada hombro con la mano opuesta sin rotar las caderas.',
        videoUrl: 'https://www.youtube.com/embed/H8j0Qh3i6-s',
        category: 'upper'
      },
      // PECHO
      {
        id: 6,
        title: 'Flexiones (push-ups)',
        description: 'ClÃ¡sico ejercicio de pecho, hombros y trÃ­ceps. Baja el cuerpo hasta casi tocar el suelo manteniendo el cuerpo recto y empuja de vuelta.',
        videoUrl: 'https://www.youtube.com/embed/IODxDxX7oi4',
        category: 'upper'
      },
      {
        id: 7,
        title: 'Press de banca (barra o mancuernas)',
        description: 'Enfoca el pectoral mayor. Acostado en banco, baja el peso controladamente al pecho y empuja hacia arriba hasta extender los brazos completamente.',
        videoUrl: 'https://www.youtube.com/embed/rT7DgCr-3pg',
        category: 'upper'
      },
      {
        id: 8,
        title: 'Aperturas con mancuernas (flyes)',
        description: 'Trabaja la apertura del pecho. Con brazos ligeramente flexionados, abre y cierra los brazos en forma de abrazo, sintiendo el estiramiento pectoral.',
        videoUrl: 'https://www.youtube.com/embed/eozdVDA78K0',
        category: 'upper'
      },
      {
        id: 9,
        title: 'Flexiones inclinadas/declinadas',
        description: 'VarÃ­an el enfoque superior/inferior del pecho. Pies elevados para trabajar pecho superior, manos elevadas para pecho inferior.',
        videoUrl: 'https://www.youtube.com/embed/cfbNhEmP-qM',
        category: 'upper'
      },
      {
        id: 10,
        title: 'Fondos en paralelas',
        description: 'Gran ejercicio para pecho y trÃ­ceps. Baja el cuerpo entre las barras paralelas flexionando los codos y empuja hacia arriba con fuerza.',
        videoUrl: 'https://www.youtube.com/embed/2z8JmcrW-As',
        category: 'upper'
      },
      // ESPALDA
      {
        id: 11,
        title: 'Dominadas o jalones al pecho',
        description: 'Fortalece dorsales y bÃ­ceps. Tira de tu cuerpo hacia arriba hasta que la barbilla supere la barra, o jala la barra hacia el pecho en mÃ¡quina.',
        videoUrl: 'https://www.youtube.com/embed/eGo4IYlbE5g',
        category: 'upper'
      },
      {
        id: 12,
        title: 'Remo con barra o mancuernas',
        description: 'Trabaja todo el dorsal medio. InclÃ­nate hacia adelante con la espalda recta y jala el peso hacia tu abdomen, apretando los omÃ³platos.',
        videoUrl: 'https://www.youtube.com/embed/kBWAon7ItDw',
        category: 'upper'
      },
      {
        id: 13,
        title: 'Remo invertido (en barra baja o TRX)',
        description: 'Con peso corporal. Cuerpo inclinado bajo una barra, tira del pecho hacia ella manteniendo el cuerpo recto como una plancha.',
        videoUrl: 'https://www.youtube.com/embed/hXTc1mDnZCw',
        category: 'upper'
      },
      {
        id: 14,
        title: 'Pull-over con mancuerna',
        description: 'Estira y activa el dorsal ancho. Acostado, lleva la mancuerna desde arriba de la cabeza hasta el pecho con los brazos extendidos.',
        videoUrl: 'https://www.youtube.com/embed/zkU6Ok44055',
        category: 'upper'
      },
      {
        id: 15,
        title: 'Superman en el suelo',
        description: 'Para zona lumbar y estabilidad. Boca abajo, eleva brazos y piernas simultÃ¡neamente del suelo, manteniendo la posiciÃ³n unos segundos.',
        videoUrl: 'https://www.youtube.com/embed/cc6UVRS7PW4',
        category: 'upper'
      },
      // BRAZOS - BÃ­ceps
      {
        id: 16,
        title: 'Curl con barra o mancuernas',
        description: 'Ejercicio fundamental para bÃ­ceps. Flexiona los codos llevando el peso hacia los hombros, manteniendo los codos fijos a los costados.',
        videoUrl: 'https://www.youtube.com/embed/ykJmrZ5v0Oo',
        category: 'upper'
      },
      {
        id: 17,
        title: 'Curl martillo',
        description: 'Trabaja bÃ­ceps y antebrazo. Mancuernas en posiciÃ³n neutral (palmas enfrentadas), flexiona los codos manteniendo esa posiciÃ³n.',
        videoUrl: 'https://www.youtube.com/embed/zC3nLlEvin4',
        category: 'upper'
      },
      {
        id: 18,
        title: 'Curl concentrado',
        description: 'Sentado, brazo apoyado en el muslo. AÃ­sla el bÃ­ceps con movimiento controlado, enfocÃ¡ndote en la contracciÃ³n mÃ¡xima.',
        videoUrl: 'https://www.youtube.com/embed/Jvj2wV0vOYU',
        category: 'upper'
      },
      // BRAZOS - TrÃ­ceps
      {
        id: 19,
        title: 'Fondos en banco',
        description: 'Manos apoyadas en banco detrÃ¡s de ti, baja y sube el cuerpo flexionando los codos, manteniendo los codos cerca del cuerpo.',
        videoUrl: 'https://www.youtube.com/embed/6kALZikXxLc',
        category: 'upper'
      },
      {
        id: 20,
        title: 'Extensiones de trÃ­ceps sobre la cabeza',
        description: 'Con mancuerna o barra, extiende los brazos desde detrÃ¡s de la cabeza hasta arriba, manteniendo los codos fijos y apuntando al techo.',
        videoUrl: 'https://www.youtube.com/embed/YbX7Wd8jQ-Q',
        category: 'upper'
      },
      {
        id: 21,
        title: 'Patada de trÃ­ceps con mancuernas',
        description: 'Inclinado hacia adelante, extiende el brazo hacia atrÃ¡s manteniendo el codo fijo a la altura del torso, contrayendo el trÃ­ceps.',
        videoUrl: 'https://www.youtube.com/embed/6SS6K3lAwZ8',
        category: 'upper'
      },
      {
        id: 22,
        title: 'Flexiones con agarre cerrado',
        description: 'Manos juntas bajo el pecho formando un diamante, enfatiza el trabajo de trÃ­ceps. Baja hasta casi tocar el suelo.',
        videoUrl: 'https://www.youtube.com/embed/_4EGPVJuqfA',
        category: 'upper'
      }
    ]
  },
  {
    id: 'lower',
    title: 'Parte Inferior del Cuerpo',
    exercises: [
      // CUÃDRICEPS
      {
        id: 23,
        title: 'Sentadillas (squats)',
        description: 'El clÃ¡sico para todo el tren inferior. Baja flexionando rodillas y caderas hasta que los muslos estÃ©n paralelos al suelo, mantÃ©n la espalda recta y el pecho arriba.',
        videoUrl: 'https://www.youtube.com/embed/ultWZbUMPL8',
        category: 'lower'
      },
      {
        id: 24,
        title: 'Zancadas o lunges',
        description: 'Adelante, atrÃ¡s o caminando. Da un paso largo y baja la rodilla trasera hacia el suelo, mantÃ©n el torso erguido y el peso en el talÃ³n delantero.',
        videoUrl: 'https://www.youtube.com/embed/QOVaHwm-Q6U',
        category: 'lower'
      },
      {
        id: 25,
        title: 'Sentadilla bÃºlgara',
        description: 'Pie trasero apoyado en un banco, enfoca una pierna. Gran ejercicio unilateral para desarrollar equilibrio y fuerza en cada pierna.',
        videoUrl: 'https://www.youtube.com/embed/2C-uSHbkBF0',
        category: 'lower'
      },
      {
        id: 26,
        title: 'Prensa de piernas (leg press)',
        description: 'Muy buena para fuerza. Empuja la plataforma con los pies desde posiciÃ³n sentada, extendiendo las piernas sin bloquear las rodillas.',
        videoUrl: 'https://www.youtube.com/embed/IZxyjW7MPJQ',
        category: 'lower'
      },
      {
        id: 27,
        title: 'Extensiones de piernas',
        description: 'En mÃ¡quina, aÃ­sla el cuÃ¡driceps. Extiende las piernas desde posiciÃ³n sentada, contrayendo el cuÃ¡driceps en la parte superior del movimiento.',
        videoUrl: 'https://www.youtube.com/embed/YyvSfVjQeL0',
        category: 'lower'
      },
      // GLÃšTEOS
      {
        id: 28,
        title: 'Hip thrust',
        description: 'El mejor para glÃºteos. Elevaciones de cadera con barra o peso, espalda apoyada en banco. Empuja la cadera hacia arriba apretando los glÃºteos.',
        videoUrl: 'https://www.youtube.com/embed/SEdqd1n0cvg',
        category: 'lower'
      },
      {
        id: 29,
        title: 'Puente de glÃºteo (glute bridge)',
        description: 'VersiÃ³n sin peso, ideal para principiantes. Acostado boca arriba, eleva la cadera del suelo apretando los glÃºteos en la parte superior.',
        videoUrl: 'https://www.youtube.com/embed/wPM8icPu6H8',
        category: 'lower'
      },
      {
        id: 30,
        title: 'Peso muerto rumano',
        description: 'Trabaja glÃºteos y femorales. Baja la barra con piernas casi rectas, empuja la cadera hacia atrÃ¡s y siente el estiramiento en los femorales.',
        videoUrl: 'https://www.youtube.com/embed/2SHsk9AzdjA',
        category: 'lower'
      },
      {
        id: 31,
        title: 'Patada de glÃºteo',
        description: 'En mÃ¡quina o en suelo con banda. AÃ­sla el glÃºteo mayor con extensiÃ³n de cadera, manteniendo la rodilla flexionada.',
        videoUrl: 'https://www.youtube.com/embed/SYJv8f40zbc',
        category: 'lower'
      },
      {
        id: 32,
        title: 'Monster walk o pasos laterales con banda',
        description: 'Con banda elÃ¡stica en las rodillas o tobillos, camina lateralmente manteniendo tensiÃ³n constante y posiciÃ³n semi-agachada.',
        videoUrl: 'https://www.youtube.com/embed/Nho2JMMIAVE',
        category: 'lower'
      },
      // ISQUIOTIBIALES
      {
        id: 33,
        title: 'Peso muerto tradicional',
        description: 'Trabaja toda la cadena posterior. Levanta la barra del suelo con espalda recta, extendiendo caderas y rodillas simultÃ¡neamente.',
        videoUrl: 'https://www.youtube.com/embed/ytGaGIn3SjE',
        category: 'lower'
      },
      {
        id: 34,
        title: 'Curl femoral',
        description: 'Acostado o sentado, con mÃ¡quina o banda. Flexiona las piernas acercando talones a glÃºteos, contrayendo los femorales.',
        videoUrl: 'https://www.youtube.com/embed/1Tq3QdYUuHs',
        category: 'lower'
      },
      {
        id: 35,
        title: 'Buenos dÃ­as (Good Mornings)',
        description: 'Con barra en hombros, inclina el torso hacia adelante manteniendo piernas casi rectas y espalda recta, siente el estiramiento femoral.',
        videoUrl: 'https://www.youtube.com/embed/YA-h3n9L4YU',
        category: 'lower'
      },
      {
        id: 36,
        title: 'Puente de glÃºteo a una pierna',
        description: 'Variante unilateral que enfatiza isquiotibiales y glÃºteos. Una pierna extendida mientras elevas la cadera con la otra.',
        videoUrl: 'https://www.youtube.com/embed/AVAXhy6pl7o',
        category: 'lower'
      },
      {
        id: 37,
        title: 'Balanceo de piernas hacia atrÃ¡s',
        description: 'De pie o en cuadrupedia, extiende la pierna hacia atrÃ¡s con control, manteniendo la espalda estable y contrayendo el glÃºteo.',
        videoUrl: 'https://www.youtube.com/embed/SFDWHBFQBuY',
        category: 'lower'
      },
      // PANTORRILLAS
      {
        id: 38,
        title: 'Elevaciones de talones de pie (calf raises)',
        description: 'Eleva el cuerpo sobre las puntas de los pies, trabaja gemelos. Puede hacerse con peso o sin Ã©l, en escalÃ³n para mayor rango.',
        videoUrl: 'https://www.youtube.com/embed/gwLzBJYoWlI',
        category: 'lower'
      },
      {
        id: 39,
        title: 'Elevaciones de talones sentado',
        description: 'En mÃ¡quina o con peso sobre las rodillas. Enfoca el sÃ³leo (parte interna de la pantorrilla), elevando los talones desde posiciÃ³n sentada.',
        videoUrl: 'https://www.youtube.com/embed/JbyjNymZOt0',
        category: 'lower'
      },
      {
        id: 40,
        title: 'Saltos en el lugar',
        description: 'Jump rope o pliometrÃ­a. Trabajo dinÃ¡mico de pantorrillas y cardio, saltando sobre las puntas de los pies con rebote explosivo.',
        videoUrl: 'https://www.youtube.com/embed/FApFLE4HyYo',
        category: 'lower'
      },
      {
        id: 41,
        title: 'Subir escalones (step-ups)',
        description: 'Con Ã©nfasis en el empuje del pie. Sube a un banco o escalÃ³n impulsÃ¡ndote con una pierna, empujando desde el talÃ³n y la punta.',
        videoUrl: 'https://www.youtube.com/embed/dQqApCGd5Ss',
        category: 'lower'
      }
    ]
  },
  {
    id: 'abs',
    title: 'Abdomen',
    exercises: [
      // ABDOMEN SUPERIOR
      {
        id: 42,
        title: 'Crunch tradicional',
