import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import { authUtils, normalizeRole, getRoleDisplayText, getRoleColor, isAdmin, isTrainer } from '../utils/auth.ts';
import api, { authService } from '../services/api.ts';

const Dashboard: React.FC = () => {
  const navigate = useNavigate();
  const [userName, setUserName] = useState<string>('');
  const [userEmail, setUserEmail] = useState<string>('');
  const [userRole, setUserRole] = useState<string>('');
  const [profileImage, setProfileImage] = useState<string>(() => {
    const savedImage = authUtils.getProfileImageUrl();
    if (savedImage) {
      const fullUrl = `${api.defaults.baseURL}${savedImage}`;
      return `${fullUrl}?t=${new Date().getTime()}`;
    }
    return `${api.defaults.baseURL}/static/default-profile.png?t=${new Date().getTime()}`;
  });
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [accountModalOpen, setAccountModalOpen] = useState(false);
  const [updateModalOpen, setUpdateModalOpen] = useState(false);
  const [updateField, setUpdateField] = useState<string>('');
  const [currentPassword, setCurrentPassword] = useState<string>('');
  const [newValue, setNewValue] = useState<string>('');
  const [deleteModalOpen, setDeleteModalOpen] = useState(false);
  const [adminPasswordModalOpen, setAdminPasswordModalOpen] = useState(false);
  const [adminPassword, setAdminPassword] = useState<string>('');

  const loadUserData = useCallback(async () => {
    const userId = localStorage.getItem('userId');
    if (!userId) {
      navigate('/');
      return;
    }

    try {
      const response = await api.get(`/usuarios/${userId}`);
      const user = response.data.usuario;
      
      setUserName(user.Nombre);
      setUserEmail(user.Email);
      setUserRole(user.Rol);
      authUtils.setUserRole(user.Rol);
      
      let imageUrl = '';
      if (user.imagen_perfil) {
        imageUrl = `${api.defaults.baseURL}${user.imagen_perfil}`;
      } else {
        imageUrl = `${api.defaults.baseURL}/static/default-profile.png`;
      }

      const timestamp = new Date().getTime();
      const finalImageUrl = `${imageUrl}?t=${timestamp}`;

      authUtils.setProfileImageUrl(user.imagen_perfil || '');
      setProfileImage(finalImageUrl);
    } catch (error) {
      console.error('Error loading user data:', error);
    }
  }, [navigate]);

  useEffect(() => {
    loadUserData();
  }, [loadUserData]);

  const handleProfileImageChange = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    const userId = localStorage.getItem('userId');
    if (!userId) return;

    try {
      const imageUrl = await authService.uploadProfileImage(parseInt(userId), file);
      const fullImageUrl = `${api.defaults.baseURL}${imageUrl}`;
      authUtils.setProfileImageUrl(imageUrl);
      
      const timestamp = new Date().getTime();
      const finalUrl = `${fullImageUrl}?t=${timestamp}`;
      setProfileImage(finalUrl);
    } catch (error) {
      console.error('Error:', error);
      alert('Error al subir la imagen');
      await loadUserData();
    }
  };

  const handleUpdateField = (field: string) => {
    setUpdateField(field);
    setCurrentPassword('');
    setNewValue('');
    setUpdateModalOpen(true);
  };

  const handleDeleteAccount = async () => {
    const userId = localStorage.getItem('userId');
    if (!userId) return;

    try {
      await authService.deleteUser(parseInt(userId));
      localStorage.clear();
      navigate('/');
    } catch (error) {
      console.error('Error deleting account:', error);
      alert('Error al eliminar la cuenta');
    }
  };

  const handleVerifyPassword = async () => {
    const userId = localStorage.getItem('userId');
    if (!userId || !currentPassword) return;

    try {
      const isValid = await authService.verifyPassword(parseInt(userId), { contrasena: currentPassword });
      if (isValid) {
        setUpdateModalOpen(true);
      } else {
        alert('Contraseña incorrecta');
      }
    } catch (error) {
      console.error('Error verifying password:', error);
      alert('Error al verificar la contraseña');
    }
  };

  const handleUpdateValue = async () => {
    const userId = localStorage.getItem('userId');
    if (!userId || !newValue) return;

    try {
      await authService.updateUser(parseInt(userId), { [updateField]: newValue });
      await loadUserData();
      setUpdateModalOpen(false);
      alert('Datos actualizados correctamente');
    } catch (error) {
      console.error('Error updating value:', error);
      alert('Error al actualizar los datos');
    }
  };

  const handleLogout = () => {
    authUtils.clearAll();
    window.location.reload();
  };

  const handleAdminPasswordSubmit = async () => {
    const ADMIN_PASSWORD = '123456789';
    if (adminPassword === ADMIN_PASSWORD) {
      const userId = localStorage.getItem('userId');
      if (userId) {
        try {
          await authService.updateUser(parseInt(userId), { rol: 'admin' });
          authUtils.setUserRole('admin');
          setUserRole('admin');
          setAdminPasswordModalOpen(false);
          setAdminPassword('');
          setAccountModalOpen(false);
          alert('¡Bienvenido Administrador!');
        } catch (error) {
          console.error('Error al cambiar a admin:', error);
          alert('Error al cambiar el rol');
        }
      }
    } else {
      alert('Contraseña incorrecta');
      setAdminPassword('');
    }
  };

  const navigateTo = (path: string) => {
    navigate(path);
  };

  return (
    <div style={{ fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif", background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)", minHeight: "100vh" }}>
      {/* Overlay */}
      {(sidebarOpen || accountModalOpen) && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            background: 'rgba(0, 0, 0, 0.5)',
            zIndex: 999
          }}
          onClick={() => {
            setSidebarOpen(false);
            setAccountModalOpen(false);
          }}
        />
      )}

      {/* Account Modal */}
      {accountModalOpen && (
        <div style={{
          position: 'fixed',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          background: 'white',
          padding: '30px',
          borderRadius: '12px',
          boxShadow: '0 10px 30px rgba(0, 0, 0, 0.4)',
          zIndex: 1001,
          maxWidth: '500px',
          width: '90%'
        }}>
          <button
            style={{
              position: 'absolute',
              top: '15px',
              right: '15px',
              background: 'none',
              border: 'none',
              fontSize: '28px',
              color: '#ccc',
              cursor: 'pointer'
            }}
            onClick={() => setAccountModalOpen(false)}
          >
            ×
          </button>
          <div style={{ fontSize: '24px', fontWeight: 700, color: '#667eea', marginBottom: '25px', borderBottom: '2px solid #667eea', paddingBottom: '10px' }}>
            👤 Mi Cuenta
          </div>

          <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginBottom: '25px' }}>
            <img 
              src={profileImage} 
              alt="Perfil" 
              data-profile-image="true"
              style={{ width: '100px', height: '100px', borderRadius: '50%', border: '4px solid #667eea', marginBottom: '10px', objectFit: 'cover' }} 
            />
            <input
              type="file"
              id="imageInput"
              accept="image/*"
              style={{ display: 'none' }}
              onChange={handleProfileImageChange}
            />
            <button
              onClick={() => document.getElementById('imageInput')?.click()}
              style={{ background: '#4ade80', color: 'white', border: 'none', padding: '8px 15px', borderRadius: '6px', cursor: 'pointer' }}
            >
              Cambiar Imagen
            </button>
          </div>

          <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '12px 0', borderBottom: '1px dashed #eee' }}>
            <span style={{ fontWeight: 600, color: '#333', width: '120px' }}>Nombre:</span>
            <span style={{ flexGrow: 1, color: '#666', marginRight: '15px' }}>{userName}</span>
            <button
              onClick={() => handleUpdateField('nombre')}
              style={{ background: '#667eea', color: 'white', border: 'none', padding: '6px 12px', borderRadius: '6px', cursor: 'pointer' }}
            >
              Actualizar
            </button>
          </div>

          <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '12px 0', borderBottom: '1px dashed #eee' }}>
            <span style={{ fontWeight: 600, color: '#333', width: '120px' }}>Email:</span>
            <span style={{ flexGrow: 1, color: '#666', marginRight: '15px' }}>{userEmail}</span>
            <button
              onClick={() => handleUpdateField('email')}
              style={{ background: '#667eea', color: 'white', border: 'none', padding: '6px 12px', borderRadius: '6px', cursor: 'pointer' }}
            >
              Actualizar
            </button>
          </div>

          <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '12px 0', borderBottom: '1px dashed #eee' }}>
            <span style={{ fontWeight: 600, color: '#333', width: '120px' }}>Contraseña:</span>
            <span style={{ flexGrow: 1, color: '#666', marginRight: '15px' }}>••••••••</span>
            <button
              onClick={() => handleUpdateField('contrasena')}
              style={{ background: '#667eea', color: 'white', border: 'none', padding: '6px 12px', borderRadius: '6px', cursor: 'pointer' }}
            >
              Actualizar
            </button>
          </div>

          <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '12px 0', borderBottom: 'none' }}>
            <span style={{ fontWeight: 600, color: '#333', width: '120px' }}>Rol:</span>
            <span style={{ 
              flexGrow: 1, 
              color: getRoleColor(userRole),
              marginRight: '15px',
              fontWeight: normalizeRole(userRole) === 'admin' ? 600 : 400
            }}>
              {getRoleDisplayText(userRole)}
            </span>
          </div>

          <div style={{ marginTop: '25px', paddingTop: '15px', borderTop: '1px solid #eee' }}>
            <button 
              onClick={() => setAdminPasswordModalOpen(true)}
              style={{ background: '#f97316', color: 'white', width: '100%', border: 'none', padding: '12px', borderRadius: '8px', cursor: 'pointer', fontSize: '16px', fontWeight: 600, marginBottom: '10px' }}>
              👑 Soy Admin
            </button>
            <button
              onClick={() => setDeleteModalOpen(true)}
              style={{ background: '#ef4444', color: 'white', width: '100%', border: 'none', padding: '12px', borderRadius: '8px', cursor: 'pointer', fontSize: '16px', fontWeight: 600 }}
            >
              🗑️ Borrar Cuenta
            </button>
          </div>

          {/* Modal de Actualización */}
          {updateModalOpen && (
            <div style={{
              position: 'fixed',
              top: '50%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
              background: 'white',
              padding: '30px',
              borderRadius: '12px',
              boxShadow: '0 10px 30px rgba(0, 0, 0, 0.4)',
              zIndex: 1002,
              width: '90%',
              maxWidth: '400px'
            }}>
              <h3 style={{ marginBottom: '20px' }}>
                {currentPassword ? 'Nuevo Valor' : 'Verificar Contraseña'}
              </h3>
              
              {!currentPassword ? (
                <>
                  <input
                    type="password"
                    placeholder="Contraseña actual"
                    value={currentPassword}
                    onChange={(e) => setCurrentPassword(e.target.value)}
                    style={{
                      width: '100%',
                      padding: '10px',
                      marginBottom: '15px',
                      border: '1px solid #ddd',
                      borderRadius: '4px'
                    }}
                  />
                  <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '10px' }}>
                    <button
                      onClick={() => setUpdateModalOpen(false)}
                      style={{
                        padding: '8px 16px',
                        background: '#e0e0e0',
                        border: 'none',
                        borderRadius: '4px',
                        cursor: 'pointer'
                      }}
                    >
                      Cancelar
                    </button>
                    <button
                      onClick={handleVerifyPassword}
                      style={{
                        padding: '8px 16px',
                        background: '#667eea',
                        color: 'white',
                        border: 'none',
                        borderRadius: '4px',
                        cursor: 'pointer'
                      }}
                    >
                      Verificar
                    </button>
                  </div>
                </>
              ) : (
                <>
                  <input
                    type={updateField === 'contrasena' ? 'password' : 'text'}
                    placeholder={`Nuevo ${updateField}`}
                    value={newValue}
                    onChange={(e) => setNewValue(e.target.value)}
                    style={{
                      width: '100%',
                      padding: '10px',
                      marginBottom: '15px',
                      border: '1px solid #ddd',
                      borderRadius: '4px'
                    }}
                  />
                  <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '10px' }}>
                    <button
                      onClick={() => {
                        setCurrentPassword('');
                        setNewValue('');
                      }}
                      style={{
                        padding: '8px 16px',
                        background: '#e0e0e0',
                        border: 'none',
                        borderRadius: '4px',
                        cursor: 'pointer'
                      }}
                    >
                      Atrás
                    </button>
                    <button
                      onClick={handleUpdateValue}
                      style={{
                        padding: '8px 16px',
                        background: '#667eea',
                        color: 'white',
                        border: 'none',
                        borderRadius: '4px',
                        cursor: 'pointer'
                      }}
                    >
                      Actualizar
                    </button>
                  </div>
                </>
              )}
            </div>
          )}

          {/* Modal de Confirmación de Borrado */}
          {deleteModalOpen && (
            <div style={{
              position: 'fixed',
              top: '50%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
              background: 'white',
              padding: '30px',
              borderRadius: '12px',
              boxShadow: '0 10px 30px rgba(0, 0, 0, 0.4)',
              zIndex: 1002,
              width: '90%',
              maxWidth: '400px'
            }}>
              <h3 style={{ marginBottom: '20px' }}>¿Estás seguro?</h3>
              <p style={{ marginBottom: '20px' }}>Esta acción no se puede deshacer.</p>
              <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '10px' }}>
                <button
                  onClick={() => setDeleteModalOpen(false)}
                  style={{
                    padding: '8px 16px',
                    background: '#e0e0e0',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer'
                  }}
                >
                  Cancelar
                </button>
                <button
                  onClick={handleDeleteAccount}
                  style={{
                    padding: '8px 16px',
                    background: '#ef4444',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer'
                  }}
                >
                  Eliminar Cuenta
                </button>
              </div>
            </div>
          )}

          {/* Modal de Contraseña de Admin */}
          {adminPasswordModalOpen && (
            <div style={{
              position: 'fixed',
              top: '50%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
              background: 'white',
              padding: '30px',
              borderRadius: '12px',
              boxShadow: '0 10px 30px rgba(0, 0, 0, 0.4)',
              zIndex: 1002,
              width: '90%',
              maxWidth: '400px'
            }}>
              <h3 style={{ marginBottom: '20px', fontSize: '20px', fontWeight: 600 }}>Ingresa la Contraseña de Admin</h3>
              <input
                type="password"
                placeholder="Contraseña"
                value={adminPassword}
                onChange={(e) => setAdminPassword(e.target.value)}
                onKeyPress={(e) => {
                  if (e.key === 'Enter') {
                    handleAdminPasswordSubmit();
                  }
                }}
                style={{
                  width: '100%',
                  padding: '12px',
                  marginBottom: '20px',
                  border: '1px solid #ddd',
                  borderRadius: '4px',
                  fontSize: '16px',
                  outline: 'none'
                }}
              />
              <div style={{ display: 'flex', justifyContent: 'flex-end', gap: '10px' }}>
                <button
                  onClick={() => {
                    setAdminPasswordModalOpen(false);
                    setAdminPassword('');
                  }}
                  style={{
                    padding: '8px 16px',
                    background: '#e0e0e0',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer'
                  }}
                >
                  Cancelar
                </button>
                <button
                  onClick={handleAdminPasswordSubmit}
                  style={{
                    padding: '8px 16px',
                    background: '#f97316',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer',
                    fontWeight: 600
                  }}
                >
                  Confirmar
                </button>
              </div>
            </div>
          )}
        </div>
      )}

      {/* Header */}
      <div style={{
        background: 'rgba(255, 255, 255, 0.1)',
        backdropFilter: 'blur(10px)',
        padding: '20px 40px',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
      }}>
        <div style={{ fontSize: '32px', fontWeight: 'bold', color: 'white', textShadow: '2px 2px 4px rgba(0, 0, 0, 0.3)' }}>
          💪 TecnoGym
        </div>
        <div
          style={{
            cursor: 'pointer',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            width: '48px',
            height: '48px',
            borderRadius: '50%',
            objectFit: 'cover',
            border: '2px solid #fff',
            boxShadow: '0 2px 6px rgba(0,0,0,0.15)'
          }}
          onClick={() => setSidebarOpen(true)}
        >
          <img 
            src={profileImage} 
            alt="Perfil" 
            data-profile-image="true"
            style={{ width: '48px', height: '48px', borderRadius: '50%', objectFit: 'cover' }} 
          />
        </div>
      </div>

      {/* Sidebar */}
      <div style={{
        position: 'fixed',
        right: sidebarOpen ? '0' : '-400px',
        top: '0',
        width: '400px',
        height: '100vh',
        background: 'white',
        boxShadow: '-4px 0 12px rgba(0, 0, 0, 0.2)',
        transition: 'right 0.3s ease',
        zIndex: 1000,
        overflowY: 'auto'
      }}>
        <button
          style={{
            position: 'absolute',
            top: '20px',
            right: '20px',
            background: 'rgba(255, 255, 255, 0.2)',
            border: 'none',
            color: 'white',
            fontSize: '24px',
            width: '40px',
            height: '40px',
            borderRadius: '50%',
            cursor: 'pointer'
          }}
          onClick={() => setSidebarOpen(false)}
        >
          ×
        </button>

        <div style={{
          position: 'relative',
          height: '200px',
          background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center'
        }}>
          <div style={{
            position: 'relative',
            cursor: 'pointer',
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            justifyContent: 'center',
            width: '100%',
            padding: '20px 0'
          }}>
            <img 
              src={profileImage} 
              alt="Perfil" 
              data-profile-image="true"
              style={{ width: '120px', height: '120px', borderRadius: '50%', objectFit: 'cover', border: '4px solid white', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)', margin: '0 auto' }} 
            />
            <div style={{ marginTop: '12px', color: 'white', fontWeight: 600, fontSize: '18px', textAlign: 'center', maxWidth: '220px', overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'nowrap' }}>
              {userRole === '%Admin%' || userRole === 'admin' ? (
                <>
                  <span style={{ color: '#FFD700' }}>{userName}</span> 👑
                </>
              ) : userRole === '!Entrenador!' || userRole === 'entrenador' ? (
                <>
                  {userName} 💪
                </>
              ) : (
                userName
              )}
            </div>
          </div>
        </div>

        <div style={{ padding: '30px' }}>
          <div style={{ padding: '15px', margin: '10px 0', background: '#f5f5f5', borderRadius: '8px', cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '12px' }} onClick={() => { setAccountModalOpen(true); setSidebarOpen(false); }}>
            <span style={{ fontSize: '24px' }}>👤</span>
            <span>Mi Cuenta</span>
          </div>

          {(userRole === '%Admin%' || userRole === 'admin') && (
            <div style={{ padding: '15px', margin: '10px 0', background: '#f5f5f5', borderRadius: '8px', cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '12px' }} onClick={() => { navigateTo('/usuarios'); setSidebarOpen(false); }}>
              <span style={{ fontSize: '24px' }}>👥</span>
              <span>Usuarios</span>
            </div>
          )}

          <div style={{ padding: '15px', margin: '10px 0', background: '#f5f5f5', borderRadius: '8px', cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '12px' }} onClick={handleLogout}>
            <span style={{ fontSize: '24px' }}>🚪</span>
            <span>Cerrar Sesión</span>
          </div>
        </div>
      </div>

      {/* Main Content - Navigation Cards */}
      <div style={{ padding: '80px 60px', maxWidth: '1800px', margin: '0 auto', minHeight: 'calc(100vh - 88px)' }}>
        <h1 style={{ color: 'white', fontSize: '56px', textAlign: 'center', marginBottom: '80px', textShadow: '2px 2px 4px rgba(0, 0, 0, 0.3)' }}>
          ¡Bienvenido, {userName}!
        </h1>
        
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(380px, 1fr))', gap: '50px', maxWidth: '1400px', margin: '0 auto' }}>
          {/* Ejercicios y Clases */}
          <div 
            onClick={() => navigateTo('/ejercicios-y-clases')}
            style={{
              background: 'white',
              borderRadius: '16px',
              padding: '40px',
              boxShadow: '0 8px 16px rgba(0, 0, 0, 0.2)',
              cursor: 'pointer',
              transition: 'transform 0.3s, box-shadow 0.3s',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              textAlign: 'center'
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.transform = 'translateY(-8px)';
              e.currentTarget.style.boxShadow = '0 12px 24px rgba(0, 0, 0, 0.3)';
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.transform = 'translateY(0)';
              e.currentTarget.style.boxShadow = '0 8px 16px rgba(0, 0, 0, 0.2)';
            }}
          >
            <div style={{ fontSize: '64px', marginBottom: '20px' }}>💪</div>
            <h2 style={{ fontSize: '24px', color: '#667eea', marginBottom: '10px', fontWeight: 600 }}>
              Ejercicios y Clases
            </h2>
            <p style={{ color: '#666', fontSize: '16px' }}>
              Explora rutinas de ejercicios y consulta el calendario de clases disponibles
            </p>
          </div>

          {/* Clases Programadas */}
          <div 
            onClick={() => navigateTo('/clases-programadas')}
            style={{
              background: 'white',
              borderRadius: '16px',
              padding: '40px',
              boxShadow: '0 8px 16px rgba(0, 0, 0, 0.2)',
              cursor: 'pointer',
              transition: 'transform 0.3s, box-shadow 0.3s',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              textAlign: 'center'
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.transform = 'translateY(-8px)';
              e.currentTarget.style.boxShadow = '0 12px 24px rgba(0, 0, 0, 0.3)';
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.transform = 'translateY(0)';
              e.currentTarget.style.boxShadow = '0 8px 16px rgba(0, 0, 0, 0.2)';
            }}
          >
            <div style={{ fontSize: '64px', marginBottom: '20px' }}>📅</div>
            <h2 style={{ fontSize: '24px', color: '#667eea', marginBottom: '10px', fontWeight: 600 }}>
              Clases Programadas
            </h2>
            <p style={{ color: '#666', fontSize: '16px' }}>
              {userRole === 'Admin' ? 'Gestiona y programa nuevas clases' : 'Busca y reserva clases disponibles'}
            </p>
          </div>

          {/* Tienda */}
          <div 
            onClick={() => navigateTo('/tienda')}
            style={{
              background: 'white',
              borderRadius: '16px',
              padding: '40px',
              boxShadow: '0 8px 16px rgba(0, 0, 0, 0.2)',
              cursor: 'pointer',
              transition: 'transform 0.3s, box-shadow 0.3s',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              textAlign: 'center'
            }}
            onMouseEnter={(e) => {
              e.currentTarget.style.transform = 'translateY(-8px)';
              e.currentTarget.style.boxShadow = '0 12px 24px rgba(0, 0, 0, 0.3)';
            }}
            onMouseLeave={(e) => {
              e.currentTarget.style.transform = 'translateY(0)';
              e.currentTarget.style.boxShadow = '0 8px 16px rgba(0, 0, 0, 0.2)';
            }}
          >
            <div style={{ fontSize: '64px', marginBottom: '20px' }}>🛒</div>
            <h2 style={{ fontSize: '24px', color: '#667eea', marginBottom: '10px', fontWeight: 600 }}>
              Tienda
            </h2>
            <p style={{ color: '#666', fontSize: '16px' }}>
              Adquiere productos, suplementos y equipamiento deportivo
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
